select  id, pledge_id, promoter_id, reward_amount, create_time, hash
from tb_pledge_promotion


        and id = #{entity.id}


        and pledge_id = #{entity.pledgeId}


        and promoter_id = #{entity.promoterId}


        and reward_amount = #{entity.rewardAmount}


        and create_time = #{entity.createTime}


        and hash = #{entity.hash}



    ORDER BY

        ${order.property} ${order.direction}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from tb_pledge_promotion


        and id = #{id}


        and pledge_id = #{pledgeId}


        and promoter_id = #{promoterId}


        and reward_amount = #{rewardAmount}


        and create_time = #{createTime}


        and hash = #{hash}
