select  id, price, stage, buyer_id, hash, address, create_time, pacakge_cnt
from tb_sj_package_tx


        and id = #{entity.id}


        and price = #{entity.price}


        and stage = #{entity.stage}


        and buyer_id = #{entity.buyerId}


        and hash = #{entity.hash}


        and address = #{entity.address}


        and create_time = #{entity.createTime}


        and pacakge_cnt = #{entity.pacakgeCnt}



    ORDER BY

        ${order.property} ${order.direction}


limit #{pageable.offset}, #{pageable.pageSize}

