mapper.select:
select id, address, token, create_time, parent_id, promo_Code
from tb_user
where id = #{id}

select  id, address, token, create_time, parent_id, promo_Code
from tb_user
where promo_Code = #{promoCode}

select  id, address, token, create_time, parent_id, promo_Code
from tb_user


        and id = #{entity.id}


        and address = #{entity.address}


        and token = #{entity.token}


        and create_time = #{entity.createTime}


        and parent_id = #{entity.parentId}


        and promo_Code = #{entity.promoCode}



    ORDER BY

        ${order.property} ${order.direction}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from tb_user


        and id = #{id}


        and address = #{address}


        and token = #{token}


        and create_time = #{createTime}


        and parent_id = #{parentId}


        and promo_Code = #{promoCode}
