select  id, address, encrypt_pwd, keystore_path, create_time
from tb_wallet


        and id = #{entity.id}


        and address = #{entity.address}


        and encrypt_pwd = #{entity.encryptPwd}


        and keystore_path = #{entity.keystorePath}


        and create_time = #{entity.createTime}



    ORDER BY

        ${order.property} ${order.direction}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from tb_wallet


        and id = #{id}


        and address = #{address}


        and encrypt_pwd = #{encryptPwd}


        and keystore_path = #{keystorePath}


        and create_time = #{createTime}
